import { gql } from '@apollo/client';

const participant = gql`
    fragment ParticipantFragment on Participant {
      id
      title
      desc
      date
      user{
        username
        email
      }
      location{
        name
        desc
        lat
        lng
      }
      participants{
        user{
          id
          username
          email
        }
      }
    }
`;

export const getEvent = gql`
  query getEvent($id: ID!){
    event(id:$id){
        ...ParticipantFragment
      }
    }
    ${participant}
`;

export const subscriptionForEvent = gql`
subscription getParticipants($eventId: ID){
  participantCreated(event_id: $eventId) {
    ...ParticipantFragment
      }
    }
    ${participant}
`;
